create table samqa.department (
    dept_no          number,
    dept_code        varchar2(20 byte),
    dept_name        varchar2(100 byte),
    creation_date    date,
    created_by       number,
    last_update_date date,
    last_updated_by  number
);

alter table samqa.department add primary key ( dept_no )
    using index enable;


