create table samqa.enrollment_edi_detail (
    detail_id                 number,
    person_type               varchar2(10 byte),
    relationship_cd           varchar2(2 byte),
    maintenance_cd            varchar2(3 byte),
    maintenance_reason        varchar2(3 byte),
    benefit_status_cd         varchar2(1 byte),
    cobra_qualifying_event    varchar2(2 byte),
    employment_status_cd      varchar2(2 byte),
    student_status_cd         varchar2(1 byte),
    handicap_ind              varchar2(1 byte),
    subscriber_number         varchar2(30 byte),
    division_cd               varchar2(30 byte),
    termination_dt            varchar2(35 byte),
    cobra_qualifying_event_dt varchar2(35 byte),
    cobra_begin_dt            varchar2(35 byte),
    cobra_end_dt              varchar2(35 byte),
    enrollment_begin_date     varchar2(35 byte),
    employment_start_date     varchar2(35 byte),
    employment_end_date       varchar2(35 byte),
    gender                    varchar2(1 byte),
    marital_status            varchar2(1 byte),
    first_name                varchar2(35 byte),
    middle_name               varchar2(35 byte),
    last_name                 varchar2(35 byte),
    address                   varchar2(110 byte),
    city                      varchar2(30 byte),
    state                     varchar2(2 byte),
    zip                       varchar2(30 byte),
    orig_system_ref           varchar2(30 byte),
    phone_work                varchar2(20 byte),
    phone_home                varchar2(20 byte),
    phone_cell                varchar2(20 byte),
    phone_extension           varchar2(6 byte),
    email                     varchar2(80 byte),
    coverage_level            varchar2(3 byte),
    coverage_maintenance_cd   varchar2(3 byte),
    benefit_begin_dt          varchar2(35 byte),
    benefit_end_dt            varchar2(35 byte),
    insurance_line_code       varchar2(3 byte),
    coverage_description      varchar2(50 byte),
    birth_date                varchar2(35 byte),
    ssn                       varchar2(80 byte),
    carrier_name              varchar2(50 byte),
    policy_number             varchar2(30 byte),
    deductible                number,
    pref_language             varchar2(80 byte),
    annual_election           varchar2(50 byte),
    payroll_frequency         varchar2(1 byte),
    contribution_amt          number,
    debit_card                varchar2(1 byte),
    status_cd                 varchar2(30 byte),
    header_id                 number,
    acc_num                   varchar2(30 byte),
    acc_id                    number,
    pers_id                   number,
    mass_enrollment_id        number,
    error_code                varchar2(255 byte),
    creation_date             date,
    last_update_date          date,
    last_updated_by           number default 0,
    batch_number              number
);

alter table samqa.enrollment_edi_detail add primary key ( detail_id )
    using index enable;


