create table samqa.giact_api_errors (
    batch_number    number,
    error_code      varchar2(100 byte),
    error_message   varchar2(2000 byte),
    error_backtrace varchar2(2000 byte),
    error_timestamp varchar2(100 byte),
    request_data    clob,
    entity_id       number
);


