    no_of_ee          number default 0,
    ga_id             number,
    acc_id            number,
    plan_start_date   date,
    plan_end_date     date,
    renewal_date      date,
    product_type      varchar2(30 byte),
    fee_paid          number,
    period_start_date date,
    period_end_date   date
);


