create global temporary table samqa.toad_plan_table (
    statement_id      varchar2(30 byte),
    plan_id           number,
    timestamp         date,
    remarks           varchar2(4000 byte),
    operation         varchar2(30 byte),
    options           varchar2(255 byte),
    object_node       varchar2(128 byte),
