    user_bank_acct_stg_id  number,
    entrp_id               number,
    batch_number           number,
    account_type           varchar2(50 byte),
    acct_usage             varchar2(50 byte),
    display_name           varchar2(255 byte),
    bank_acct_type         varchar2(15 byte),
    bank_routing_num       varchar2(9 byte),
